import React, { Component } from 'react';
import './ticketHomePage.css';

import { connect } from "react-redux";
import { completeToDo } from "../actions";

import {TICKET_STATUS_TYPES} from './ticketStatus';

class Ticket extends Component {

  handleCompleteClick = completeTicketId => {
    const { completeToDo } = this.props;
    completeToDo(completeTicketId);
  };

  classes = (status) => {
    switch(status){
      case 'Pending':
        return 'red';
      case 'Started':
        return 'yellow';
      case 'In Review':
        return 'yellow';
      case 'Done':
        return 'green';
      default:
        return 'red';
    }
  };

  render() {
    const { ticketId, title, description, owner, status } = this.props;
    return (
      <div className={`single-ticket-container ticket-row-${this.classes()}`}>
        <div className='ticket-text-container'>
          <h4>
            <span
              onClick={() => this.handleCompleteClick(ticketId)}
              className="complete-todo-item waves-effect waves-light teal lighten-5 teal-text text-darken-4 btn"
            >
              <i className="large material-icons">done</i>
            </span>
          </h4>

          <div className='ticket-text-spacer'>{ title }</div>
          <div className='ticket-text-spacer'>{ description }</div>
          <div className='ticket-text-spacer'>{ owner }</div>
          <div className='ticket-text-spacer'>Status: { status }</div>

        </div>
      </div>
    );
  }
}

export default connect(null, { completeToDo })(Ticket);
