import "./ToDoList.css";
import React, { Component } from 'react';
import { connect } from "react-redux";
import _ from "lodash";
import * as actions from "../actions";
import TicketColumn from './TicketColumn';

class ToDoList extends Component {
  state = {
    addFormVisible: false,
    addFormValue: ""
  };

  handleInputChange = event => {
    this.setState({ addFormValue: event.target.value });
  };

  handleFormSubmit = event => {
    const { addFormValue } = this.state;
    const { addTicket } = this.props;
    event.preventDefault();
    addTicket({ title: addFormValue });
    this.setState({ addFormValue: "" });
  };

  renderAddForm = () => {
    const { addFormVisible, addFormValue } = this.state;
    if (addFormVisible) {
      return (
        <div id="todo-add-form" className="col s10 offset-s1">
          <form onSubmit={this.handleFormSubmit}>
            <div className="input-field">
              <i className="material-icons prefix">note_add</i>
              <input
                value={addFormValue}
                onChange={this.handleInputChange}
                id="toDoNext"
                type="text"
              />
              <label htmlFor="toDoNext">What To Do Next</label>
            </div>
          </form>
        </div>
      );
    }
  };

  renderToDos() {
    const { data } = this.props;
    // const tickets = _.map(data, (value, key) => {
    //   return <ToDoListItem key={key} todoId={key} todo={value} />;
    // });

    const pendingTickets = [];
    const startedTickets = [];
    const reviewingTickets = [];
    const doneTickets = [];

    _.map(data, (value) => {
      switch (value.status) {
        case 'Pending':
          pendingTickets.push(value)
          break;
        case 'Started':
          startedTickets.push(value)
          break;
        case 'In Review':
          reviewingTickets.push(value)
          break;
        case 'Done':
          doneTickets.push(value)
          break;
        default:
          break;
      }
    });

    const ticket_columns = () => {
      return (
        <>
          <TicketColumn
            key={key}
            title='Pending'
            color='grey'
            tickets={pendingTickets}
          />
          <TicketColumn
            key={key}
            title='Started'
            color='grey'
            tickets={startedTickets}
          />
          <TicketColumn
            key={key}
            title='In Review'
            color='grey'
            tickets={reviewingTickets}
          />
          <TicketColumn
            key={key}
            title='Done'
            color='grey'
            tickets={doneTickets}
          />
        </>
      );
    };

    if (!_.isEmpty(ticket_columns)) {
      return ticket_columns;
    }
    return (
      <div className="col s10 offset-s1 center-align">
        <img
          alt="Nothing was found"
          id="nothing-was-found"
          src="/img/nothing.png"
        />
        <h4>You have completed all the tickets</h4>
        <p>Start by clicking add button in the bottom of the screen</p>
      </div>
    );
  }

  componentWillMount() {
    this.props.fetchToDos();
  }

  render() {
    const { addFormVisible } = this.state;
    return (
      <div className="to-do-list-container">
        <div className="row">
          {this.renderAddForm()}
          {this.renderToDos()}
        </div>
        <div className="fixed-action-btn">
          <button
            onClick={() => this.setState({ addFormVisible: !addFormVisible })}
            className="btn-floating btn-large teal darken-4"
          >
            {addFormVisible ? (
              <i className="large material-icons">close</i>
            ) : (
              <i className="large material-icons">add</i>
            )}
          </button>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ data }) => {
  return {
    data
  };
};

export default connect(mapStateToProps, actions)(ToDoList);
